<?php

if (!defined('ABSPATH')) exit; // Exit if accessed directly

use Elementor\Widget_Base;
use Elementor\Controls_Manager;
use Elementor\Utils;

class ALQA_SIGNALS_Awareness_Widget extends Widget_Base
{
    public function get_name()
    {
        return 'alqa_signals_awareness';
    }

    public function get_title()
    {
        return __('Development & Awareness Section', 'alqa_signals');
    }

    public function get_icon()
    {
        return 'eicon-lightbulb';
    }

    public function get_categories()
    {
        return ['general'];
    }

    protected function _register_controls()
    {
        // === Section: Content ===
        $this->start_controls_section(
            'section_awareness_content',
            [
                'label' => __('Section Content', 'alqa_signals'),
            ]
        );

        // Title
        $this->add_control(
            'awareness_title',
            [
                'label' => __('Section Title', 'alqa_signals'),
                'type' => Controls_Manager::TEXT,
                'default' => __('Development & [highlight]Awareness[/highlight]', 'alqa_signals'),
                'description' => __('Wrap a word in [highlight][/highlight] to make it highlighted (e.g., Development & [highlight]Awareness[/highlight])', 'alqa_signals'),
            ]
        );

        // Description
        $this->add_control(
            'awareness_description',
            [
                'label' => __('Description', 'alqa_signals'),
                'type' => Controls_Manager::TEXTAREA,
                'default' => __('We develop data-centric educational platforms and awareness programs that turn complexity into clarity.', 'alqa_signals'),
            ]
        );

        // Subtitle
        $this->add_control(
            'awareness_subtitle',
            [
                'label' => __('Subtitle', 'alqa_signals'),
                'type' => Controls_Manager::TEXT,
                'default' => __('Discoveries', 'alqa_signals'),
            ]
        );

        // Features list
        $this->add_control(
            'awareness_features',
            [
                'label' => __('Features List', 'alqa_signals'),
                'type' => Controls_Manager::REPEATER,
                'prevent_empty' => false,
                'fields' => [
                    [
                        'name' => 'feature_item',
                        'label' => __('Feature', 'alqa_signals'),
                        'type' => Controls_Manager::TEXT,
                        'default' => __('Feature Text', 'alqa_signals'),
                    ],
                ],
                'default' => [
                    ['feature_item' => 'Innovative analysis tools'],
                    ['feature_item' => 'Interactive learning modules'],
                    ['feature_item' => 'Comprehensive market insights'],
                ],
                'title_field' => '{{{ feature_item }}}',
            ]
        );

        // CTA Label
        $this->add_control(
            'awareness_cta_label',
            [
                'label' => __('Button Label', 'alqa_signals'),
                'type' => Controls_Manager::TEXT,
                'default' => __('Explore More', 'alqa_signals'),
            ]
        );

        // CTA Link
        $this->add_control(
            'awareness_cta_link',
            [
                'label' => __('Button Link', 'alqa_signals'),
                'type' => Controls_Manager::URL,
                'placeholder' => __('https://your-link.com', 'alqa_signals'),
                'default' => [
                    'url' => '#',
                ],
            ]
        );

        // CTA Special Style
        $this->add_control(
            'awareness_cta_special',
            [
                'label' => __('Special Button Style', 'alqa_signals'),
                'type' => Controls_Manager::SWITCHER,
                'label_on' => __('Special', 'alqa_signals'),
                'label_off' => __('Default', 'alqa_signals'),
                'return_value' => 'yes',
                'default' => 'no',
            ]
        );

        $this->end_controls_section();

        // === Section: Images ===
        $this->start_controls_section(
            'section_awareness_images',
            [
                'label' => __('Images', 'alqa_signals'),
            ]
        );

        // Big Image
        $this->add_control(
            'awareness_image_big',
            [
                'label' => __('Main Image', 'alqa_signals'),
                'type' => Controls_Manager::MEDIA,
                'default' => [
                    'url' => Utils::get_placeholder_image_src(),
                ],
            ]
        );

        // Small Image
        $this->add_control(
            'awareness_image_small',
            [
                'label' => __('Secondary Image', 'alqa_signals'),
                'type' => Controls_Manager::MEDIA,
                'default' => [
                    'url' => Utils::get_placeholder_image_src(),
                ],
            ]
        );

        $this->end_controls_section();
    }

    protected function render()
    {
        $settings = $this->get_settings_for_display();

        // Parse highlight shortcode
        $title = preg_replace('/\[highlight\](.*?)\[\/highlight\]/', '<span>$1</span>', $settings['awareness_title']);
?>
        <section class="section_awareness">
            <div class="container">
                <div class="row">
                    <!-- Left Column -->
                    <div class="col-lg-6">
                        <div class="sec_head wow fadeInUp">
                            <h2><?php echo wp_kses_post($title); ?></h2>
                            <p><?php echo esc_html($settings['awareness_description']); ?></p>
                        </div>

                        <div class="dta-projects wow fadeInUp">
                            <p><?php echo esc_html($settings['awareness_subtitle']); ?></p>

                            <?php if (!empty($settings['awareness_features'])) : ?>
                                <ul>
                                    <?php foreach ($settings['awareness_features'] as $feature) : ?>
                                        <li><strong><?php echo esc_html($feature['feature_item']); ?></strong></li>
                                    <?php endforeach; ?>
                                </ul>
                            <?php endif; ?>

                            <?php if (!empty($settings['awareness_cta_label'])) : ?>
                                <div class="explore-awareness">
                                    <a href="<?php echo esc_url($settings['awareness_cta_link']['url']); ?>"
                                        class="<?php echo ($settings['awareness_cta_special'] === 'yes') ? ' btn-special' : 'btn-site'; ?>">
                                        <?php echo esc_html($settings['awareness_cta_label']); ?>
                                    </a>
                                </div>
                            <?php endif; ?>
                        </div>
                    </div>

                    <!-- Right Column -->
                    <div class="col-lg-6">
                        <div class="thumb-awareness wow fadeInUp">
                            <?php if (!empty($settings['awareness_image_big']['url'])) : ?>
                                <figure class="awareness-big">
                                    <img src="<?php echo esc_url($settings['awareness_image_big']['url']); ?>" alt="Awareness Image" />
                                </figure>
                            <?php endif; ?>

                            <?php if (!empty($settings['awareness_image_small']['url'])) : ?>
                                <figure class="awareness-sml">
                                    <img src="<?php echo esc_url($settings['awareness_image_small']['url']); ?>" alt="Awareness Image Small" />
                                </figure>
                            <?php endif; ?>

